allow:
Bash(awk '
BEGIN { print """"."""" }
{
    depth = gsub(/\//, """"/"""", $0)
    indent = """"""""
    for (i = 0; i < depth; i++) indent = indent """"│   """"

    split($0, parts, """"/"""")
    name = parts[length(parts)]

    if ($0 ~ /\/$/ || system(""""test -d \"""""""" $0 """"\"""""""") == 0) {
        print indent """"├── """" name """"/""""
    } else {
        print indent """"├── """" name
    }
}')
